/**
 * Wraps the connection to HANA in a Promise.
 *
 * @param {HDB_Client} hdb_connection Client
 * @returns {Promise} Promise
 */
function promise_connect(hdb_connection){
  return new Promise((resolve, reject) => {
    hdb_connection.connect((e) => {
      if (e){
        reject(e);
      }

      resolve();
    });
  });
}

