    const sql = `call ${this.apiSchema}.${command}(${this.paramSchema}.${CONSTANTS.OBJECTS_TABLE}, ${this.paramSchema}.${CONSTANTS.PARAMETERS_TABLE}, ?, ?, ?, ?)`;
